import styled from 'styled-components';

export const Container = styled.nav`
    padding: 1rem 5rem;
    display: flex;
    align-items: center;
    justify-content: start;
    flex-wrap: wrap;
    div{
      display: flex;
      justify-content: space-between;
      align-items: center;
      position: relative;

      @media (max-width: 798px){
        overflow: hidden;
        flex-direction: column;
        width: 100%;
      }


      a{
        padding: 0 2rem;
        text-align: center;
        font-size: 1rem;
        font-weight: 600;
        color: #071242;
        text-decoration: none;
        cursor: pointer; 
      }


    };
`

export const HamburguerMenu = styled.div`
  display: flex;
  flex-direction: column;
  cursor: pointer;

  span{
    height: 2px;
    width: 25px;
    background: #071242;
    margin-bottom: 4px;
    border-radius: 5px;
  }

  @media (max-width: 768px){
    display: flex;
  }
`
