import styled from 'styled-components';

export const Container = styled.nav`
    padding: 1rem 1rem;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-wrap: wrap;

    img{
      width: 150px;
      margin-right: 2rem;
    }
`

export const Menu = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: relative;

  @media (max-width: 798px){
    overflow: hidden;
    flex-direction: column;
    width: 100%;
    elevation: 1;
    max-height: ${props => (props.status ? "300px": 0)};
    transition: max-height 0.3s ease-in;
  };
  a{
    padding: 1rem 0.8rem;
    text-align: center;
    font-size: 1rem;
    font-weight: 600;
    color: #071242;
    text-decoration: none;
    cursor: pointer; 
  }
`

export const HamburguerMenu = styled.div`
  display: none;
  flex-direction: column;
  cursor: pointer;
  margin-left: auto;
  span{
    height: 2px;
    width: 25px;
    background: #071242;
    margin-bottom: 4px;
    border-radius: 5px;
  }

  @media (max-width: 768px){
    display: flex;
  }
`
